import { Router } from 'express';
import Userdb from '@/app/schemas/model'

const router = new Router();

router.post('/', (req, res) => { // criar funcionario
    const {  nome, email, cpf } = req.body;
    Userdb.create({nome, email, cpf})
    .then(Userdb => {
        res.status(200).send(Userdb);
    })
    .catch(error=> {
        console.error('Erro ao salvar poxeto', error)
        res
            .status(400)
            .send({
                error:
                    'nao foi possivel salvar seu poxeto'

            });
    });
    
});

router.get('/', (req, res) => {
    Userdb.find()
        .then(data =>{
            const funcionario = data.map(Userdb =>{
                return { nome: Userdb.nome, cpf: Userdb.cpf, 
                email: Userdb.email}
            });
            res.send(funcionario);
        })
        .catch(error => {
            console.error('erro ao recuperar dados.');
            res.status(400).send({
                error: 'não foi possible pegar dados.'
            });
        });

});
router.get('/id/:UserdbId', (req, res) => {
    Userdb.findById(req.params.UserdbId)
        .then(Userdb =>{
            res.send(Userdb);
        })
        .catch(error => {
            console.error('erro ao recuperar dados.', error);
            res.status(400).send({
                error: 'não foi possible pegar dados.'
            });
        });

});


router.put('/:UserdbId', (req, res) => {
    const { nome, cpf, email} = req.body;

    Userdb.findByIdAndUpdate(req.params.UserdbId, 
        {nome, cpf, email}, {new: true})
    .then(Userdb =>{
        res.status(200).send(Userdb);
    })
    .catch(error => {
        console.error('Erro ao atualizar dados', error);
        res.status(400).send({
            error:
                'Não foi possivel atualizar amor',
        });
    });
});

router.delete('/:UserdbId', (req, res) => {
    Userdb.findByIdAndRemove(req.params.UserdbId)
        .then(() => {
            res.send({message: 'Funcionario removido com sucesso'});
        })
        .catch(error => {
            console.error('Erro ao remover funcionario do BD', error)
            res.status(400).send({message: 'Erro ao excluir'})
        })
});

export default router;
