import {Router} from 'express';

const router = new Router();

router.post('/', (req, res) => {
    if(!req.body){
        res.status(400).send({message: "nao pode ser vazio"});
        return;
    }
    // novo funcionario
    const funcionario = new Userdb({
        nome: req.body.nome,
        email: req.body.email,
        cpf: req.body.cpf,
    })

    // salvar funcionario no banco de dados
    funcionario
    .save(funcionario)
    .then(data=>{
        res.send(data)
    })
    .catch(err=>{
        res.status(500).send({
            message: "erro ocorreu ao salvar"
        })
    })
})

